// Wait until the entire page (images, scripts, etc.) is fully loaded
window.addEventListener("load", function () {
    var preloader = document.getElementById("preloader");

    // Add the fade-out class to trigger the CSS transition
    preloader.classList.add("fade-out");

    // Remove the preloader from the DOM after the fade-out transition completes
    setTimeout(function () {
        preloader.parentNode.removeChild(preloader);
    }, 500); // This delay should match the CSS transition duration
});
